<?phpnamespace app\models;use yii\base\Model;class Signup extends Model{    public $nickname;    public $password;    public $email;    public $name;    public function rules()    {        return [          [['nickname','password', 'email','name'], 'required'],            ['email', 'email'],            ['nickname','unique', 'targetClass'=>'app\models\User'],            ['email','unique', 'targetClass'=>'app\models\User'],            ['password','string','min'=>2,'max'=>10],        ];    }    public function signup(){        $user = new User;        $user->nickname = $this->nickname;        $user->setPassword($this->password);        $user->email = $this->email;        $user->name = $this->name;        return $user->save();    }}